import React, {useMemo} from "react";
import styles from "./ingredients.module.css";
import {data} from "../../../utils/data";
import {CurrencyIcon} from "@ya.praktikum/react-developer-burger-ui-components";

const Ingredients = () => {

    // const bun = data.filter(bun =>
    //     bun.type === 'bun');
    // const main = data.filter(main =>
    //     main.type === 'main');
    // const sauce = data.filter(sauce =>
    //     sauce.type === 'sauce');

    const typeFilter = data.filter((data =>
    data.type === 'bun' && data.type === 'main' && data.type === 'sauce')
    );


    return (
        <div className={styles.mainIngredient}>
            <div className={styles.ingredients}>
                {typeFilter(data).map(data => (
                    <ul key={data._id + data.type}>
                        <img src={data.image} alt={'img'}/>
                        <li>{data.price}
                            <CurrencyIcon type={"primary"}/>
                        </li>
                        <li>{data.name}</li>
                    </ul>
                ))}
            </div>
        </div>
    );
}

export default Ingredients;
