import React, {useMemo, useState} from "react";
import styles from "./ingredients.module.css";
import {data} from "../../../utils/data";
import {CurrencyIcon} from "@ya.praktikum/react-developer-burger-ui-components";
import Modal from "../../modal/modal";

const Ingredients = () => {

    const [isOpen, setIsOpen] = useState(false);

    // const [ingredient, setIngredient] = useState([])
    // const buns = useMemo(() => setIngredient(ingredient => ingredient.type === 'bun'), [data]);

    const filterIngredients = data.filter(ingredient => {
        return (
        ingredient.type === 'bun' || ingredient.type === 'main' || ingredient.type === 'sauce'
        );
    })

    return (
        <div className={styles.mainIngredient}>
            <div className={styles.ingredients}>
                <div onClick={() => setIsOpen(true)}>
                    {filterIngredients.map(ingredient => (
                    <li className={styles.liArray} key={ingredient._id}>
                        <h2>{ingredient}</h2>
                        {data.map(data => (
                            <li className={styles.liIngredients} key={data._id}>
                                <img src={data.image} alt={'img'}/>
                                <li className={'text text_type_digits-default'}>{data.price}
                                    <CurrencyIcon type={"primary"}/>
                                </li>
                                <li className={'text text_type_main-default'}>{data.name}</li>
                            </li>
                        ))}
                    </li>
                    ))}

                </div>
            </div>
            {isOpen && <Modal setIsOpen={setIsOpen}/>}
        </div>
    );
}

export default Ingredients;
