import React, {useMemo, useState} from "react";
import styles from './burger-ingredients.module.css';
import {Tab} from "@ya.praktikum/react-developer-burger-ui-components";
import Ingredients from "./ingredients/ingredients";
import {data} from "../../utils/data";

const BurgerIngredients = () => {
    const [current, setCurrent] = React.useState('bun')


    const [ingredients, setIngredients] = useState([])
    const buns = useMemo(() => data.filter(ingredients => ingredients.type === 'bun'), [data]);

    // const bun = data.filter(bun =>
    //     bun.type === 'bun');
    // const main = data.filter(main =>
    //     main.type === 'main');
    // const sauce = data.filter(sauce =>
    //     sauce.type === 'sauce');


    return (
        <div className={styles.mainBurgerIngredients}>

            <h1>Соберите бургер</h1>

            <div style={{ display: 'flex' }}>
                <Tab value="bun" active={current === 'bun'} onClick={setCurrent}>
                    Булки
                </Tab>
                <Tab value="main" active={current === 'main'} onClick={setCurrent}>
                    Соусы
                </Tab>
                <Tab value="sauce" active={current === 'sauce'} onClick={setCurrent}>
                    Начинки
                </Tab>
            </div>
            <Ingredients buns={buns}/>
        </div>
    );
}

export default BurgerIngredients;
