import styles from './app-header.module.css';
import {BurgerIcon, ListIcon, Logo, ProfileIcon} from "@ya.praktikum/react-developer-burger-ui-components";
import {hover} from "@testing-library/user-event/dist/hover";
import {useState} from "react";


const AppHeader = () => {

    const [open, setOpen] = useState(false);

    return (
        <header>
            <div className={styles.containerHeader}>
                <nav>
                    {/*Конструктор*/}
                    <a className={styles.aConstructor} onMouseOver={() => setOpen(true)} onMouseOut={() => setOpen(false)}>
                        <BurgerIcon type={open ? 'primary' : 'secondary'}/>
                        <p className={`ml-2`}>Конструктор</p>
                    </a>
                    {/*Лента заказов*/}
                    <a className={styles.aLineOrder} onMouseOver={() => setOpen(true)} onMouseOut={() => setOpen(false)}>
                        <ListIcon type={open ? 'primary' : 'secondary'}/>
                        <p className={`ml-2`}>Лента заказов</p>
                    </a>
                </nav>
                {/*Лого*/}
                <a className={styles.logo}>
                    <Logo/>
                </a>
                {/*Личный кабинет*/}
                <span className={styles.lk}>
                <a className={styles.lkContainer} onMouseOver={() => setOpen(true)} onMouseOut={() => setOpen(false)}>
                    <ProfileIcon type={open ? 'primary' : 'secondary'}/>
                    <p className={`ml-2`}>Личный кабинет</p>
                </a>
                </span>
            </div>
        </header>
    );
}

export default AppHeader;
